  children,
}: {
  children: React.ReactNode
}) {
  return (
    <html lang="en">
      <body className={inter.className}>
        <ErrorBoundary>
          <NextAuthSessionProvider>
            <DemoDataProvider>
              <NavigationProgress />
              <Navigation />
              {children}
